import React, { useRef } from 'react';

function RefForm() {
  const nombreRef = useRef(null);
  const descripcionRef = useRef(null);
  const subcuentaRef = useRef(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = {
      nombre: nombreRef.current.value,
      descripcion: descripcionRef.current.value,
      subcuenta: subcuentaRef.current.value
    };
    console.log('Form submitted:', formData);
    alert('Formulario enviado: ' + JSON.stringify(formData, null, 2));
  };

  return (
    <div style={{ padding: '20px', maxWidth: '600px', margin: '0 auto' }}>
      <h2>Formulario con useRef - Prueba de Escritura</h2>
      <p style={{ color: '#666', marginBottom: '20px' }}>
        Este formulario usa useRef en lugar de estado controlado. 
        Debería permitir escribir sin problemas.
      </p>
      
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '15px' }}>
          <label htmlFor="nombre" style={{ display: 'block', marginBottom: '5px' }}>
            Nombre:
          </label>
          <input
            type="text"
            id="nombre"
            ref={nombreRef}
            style={{
              width: '100%',
              padding: '10px',
              border: '2px solid #ddd',
              borderRadius: '5px',
              fontSize: '16px'
            }}
            placeholder="Escribe aquí para probar (useRef)..."
          />
        </div>

        <div style={{ marginBottom: '15px' }}>
          <label htmlFor="descripcion" style={{ display: 'block', marginBottom: '5px' }}>
            Descripción:
          </label>
          <textarea
            id="descripcion"
            ref={descripcionRef}
            rows="3"
            style={{
              width: '100%',
              padding: '10px',
              border: '2px solid #ddd',
              borderRadius: '5px',
              fontSize: '16px'
            }}
            placeholder="Escribe una descripción aquí (useRef)..."
          />
        </div>

        <div style={{ marginBottom: '15px' }}>
          <label htmlFor="subcuenta" style={{ display: 'block', marginBottom: '5px' }}>
            Subcuenta (máx 3 caracteres):
          </label>
          <input
            type="text"
            id="subcuenta"
            ref={subcuentaRef}
            maxLength="3"
            style={{
              width: '100%',
              padding: '10px',
              border: '2px solid #ddd',
              borderRadius: '5px',
              fontSize: '16px'
            }}
            placeholder="001"
          />
        </div>

        <button
          type="submit"
          style={{
            backgroundColor: '#28a745',
            color: 'white',
            padding: '10px 20px',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            fontSize: '16px'
          }}
        >
          Enviar Formulario (useRef)
        </button>
      </form>
    </div>
  );
}

export default RefForm;
