import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Configuración de la API
const API_BASE_URL = 'http://localhost:8000/api';
axios.defaults.baseURL = API_BASE_URL;

function App() {
  const [currentView, setCurrentView] = useState('home');
  const [departamentos, setDepartamentos] = useState([]);
  const [puestos, setPuestos] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Estados para formularios
  const [formData, setFormData] = useState({
    nombre: '',
    descripcion: '',
    subcuenta: ''
  });
  const [editingId, setEditingId] = useState(null);

  // Cargar datos
  const loadDepartamentos = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/departamentos');
      setDepartamentos(response.data);
    } catch (err) {
      setError('Error al cargar departamentos');
    } finally {
      setLoading(false);
    }
  };

  const loadPuestos = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/puestos');
      setPuestos(response.data);
    } catch (err) {
      setError('Error al cargar puestos');
    } finally {
      setLoading(false);
    }
  };

  // Manejar cambios en formularios
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Guardar departamento
  const saveDepartamento = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      if (editingId) {
        await axios.put(`/departamentos/${editingId}`, formData);
      } else {
        await axios.post('/departamentos', formData);
      }
      setFormData({ nombre: '', descripcion: '', subcuenta: '' });
      setEditingId(null);
      loadDepartamentos();
      setCurrentView('departamentos');
    } catch (err) {
      setError('Error al guardar departamento');
    } finally {
      setLoading(false);
    }
  };

  // Guardar puesto
  const savePuesto = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      if (editingId) {
        await axios.put(`/puestos/${editingId}`, formData);
      } else {
        await axios.post('/puestos', formData);
      }
      setFormData({ nombre: '' });
      setEditingId(null);
      loadPuestos();
      setCurrentView('puestos');
    } catch (err) {
      setError('Error al guardar puesto');
    } finally {
      setLoading(false);
    }
  };

  // Eliminar departamento
  const deleteDepartamento = async (id) => {
    if (window.confirm('¿Estás seguro de eliminar este departamento?')) {
      try {
        await axios.delete(`/departamentos/${id}`);
        loadDepartamentos();
      } catch (err) {
        setError('Error al eliminar departamento');
      }
    }
  };

  // Eliminar puesto
  const deletePuesto = async (id) => {
    if (window.confirm('¿Estás seguro de eliminar este puesto?')) {
      try {
        await axios.delete(`/puestos/${id}`);
        loadPuestos();
      } catch (err) {
        setError('Error al eliminar puesto');
      }
    }
  };

  // Editar departamento
  const editDepartamento = (departamento) => {
    setFormData({
      nombre: departamento.nombre,
      descripcion: departamento.descripcion || '',
      subcuenta: departamento.subcuenta
    });
    setEditingId(departamento.id);
    setCurrentView('departamento-form');
  };

  // Editar puesto
  const editPuesto = (puesto) => {
    setFormData({
      nombre: puesto.nombre
    });
    setEditingId(puesto.id);
    setCurrentView('puesto-form');
  };

  // Cargar datos al cambiar de vista
  useEffect(() => {
    if (currentView === 'departamentos') {
      loadDepartamentos();
    } else if (currentView === 'puestos') {
      loadPuestos();
    }
  }, [currentView]);

  // Componente Home
  const Home = () => (
    <div className="min-vh-100 d-flex align-items-center justify-content-center" 
         style={{
           background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
           minHeight: '100vh'
         }}>
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-lg-8 col-md-10">
            <div className="card shadow-lg border-0" 
                 style={{
                   borderRadius: '20px',
                   backdropFilter: 'blur(10px)',
                   backgroundColor: 'rgba(255, 255, 255, 0.95)'
                 }}>
              <div className="card-body text-center py-5 px-4">
                <h1 className="display-4 fw-bold mb-4" 
                    style={{ color: '#2c3e50' }}>
                  Sistema de Gestión
                </h1>
                <p className="lead mb-5" 
                   style={{ color: '#7f8c8d' }}>
                  Administra departamentos y puestos de manera eficiente
                </p>
                <div className="row g-4">
                  <div className="col-md-6">
                    <div className="d-grid">
                      <button 
                        className="btn btn-lg text-white fw-bold py-3 px-4"
                        style={{
                          background: 'linear-gradient(45deg, #3498db, #2980b9)',
                          border: 'none',
                          borderRadius: '15px',
                          boxShadow: '0 8px 25px rgba(52, 152, 219, 0.3)',
                          transition: 'all 0.3s ease'
                        }}
                        onMouseOver={(e) => {
                          e.target.style.transform = 'translateY(-2px)';
                          e.target.style.boxShadow = '0 12px 35px rgba(52, 152, 219, 0.4)';
                        }}
                        onMouseOut={(e) => {
                          e.target.style.transform = 'translateY(0)';
                          e.target.style.boxShadow = '0 8px 25px rgba(52, 152, 219, 0.3)';
                        }}
                        onClick={() => setCurrentView('departamentos')}
                      >
                        <i className="fas fa-building me-2"></i>
                        Gestión de Departamentos
                      </button>
                    </div>
                  </div>
                  <div className="col-md-6">
                    <div className="d-grid">
                      <button 
                        className="btn btn-lg text-white fw-bold py-3 px-4"
                        style={{
                          background: 'linear-gradient(45deg, #27ae60, #2ecc71)',
                          border: 'none',
                          borderRadius: '15px',
                          boxShadow: '0 8px 25px rgba(39, 174, 96, 0.3)',
                          transition: 'all 0.3s ease'
                        }}
                        onMouseOver={(e) => {
                          e.target.style.transform = 'translateY(-2px)';
                          e.target.style.boxShadow = '0 12px 35px rgba(39, 174, 96, 0.4)';
                        }}
                        onMouseOut={(e) => {
                          e.target.style.transform = 'translateY(0)';
                          e.target.style.boxShadow = '0 8px 25px rgba(39, 174, 96, 0.3)';
                        }}
                        onClick={() => setCurrentView('puestos')}
                      >
                        <i className="fas fa-briefcase me-2"></i>
                        Gestión de Puestos
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  // Componente Lista de Departamentos
  const DepartamentoList = () => (
    <div className="container-fluid">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <h2 className="mb-1" style={{ color: '#2c3e50' }}>Departamentos</h2>
          <p className="text-muted mb-0">Gestiona los departamentos de tu organización</p>
        </div>
        <button 
          className="btn btn-primary btn-lg px-4 py-2"
          style={{
            background: 'linear-gradient(45deg, #3498db, #2980b9)',
            border: 'none',
            borderRadius: '10px',
            boxShadow: '0 4px 15px rgba(52, 152, 219, 0.3)'
          }}
          onClick={() => {
            setFormData({ nombre: '', descripcion: '', subcuenta: '' });
            setEditingId(null);
            setCurrentView('departamento-form');
          }}
        >
          <i className="fas fa-plus me-2"></i>
          Nuevo Departamento
        </button>
      </div>

      {loading && (
        <div className="d-flex justify-content-center py-5">
          <div className="spinner-border text-primary" role="status" style={{ width: '3rem', height: '3rem' }}>
            <span className="visually-hidden">Cargando...</span>
          </div>
        </div>
      )}

      <div className="card shadow-sm border-0" style={{ borderRadius: '15px' }}>
        <div className="card-body p-0">
          {departamentos.length > 0 ? (
            <div className="table-responsive">
              <table className="table table-hover mb-0">
                <thead style={{ backgroundColor: '#f8f9fa' }}>
                  <tr>
                    <th className="border-0 py-3 px-4" style={{ color: '#495057', fontWeight: '600' }}>ID</th>
                    <th className="border-0 py-3 px-4" style={{ color: '#495057', fontWeight: '600' }}>Nombre</th>
                    <th className="border-0 py-3 px-4" style={{ color: '#495057', fontWeight: '600' }}>Descripción</th>
                    <th className="border-0 py-3 px-4" style={{ color: '#495057', fontWeight: '600' }}>Subcuenta</th>
                    <th className="border-0 py-3 px-4 text-center" style={{ color: '#495057', fontWeight: '600' }}>Acciones</th>
                  </tr>
                </thead>
                <tbody>
                  {departamentos.map((departamento, index) => (
                    <tr key={departamento.id} style={{ 
                      backgroundColor: index % 2 === 0 ? '#ffffff' : '#f8f9fa',
                      transition: 'all 0.2s ease'
                    }}>
                      <td className="py-3 px-4" style={{ color: '#6c757d' }}>{departamento.id}</td>
                      <td className="py-3 px-4">
                        <span className="fw-semibold" style={{ color: '#2c3e50' }}>{departamento.nombre}</span>
                      </td>
                      <td className="py-3 px-4" style={{ color: '#6c757d' }}>
                        {departamento.descripcion || <span className="text-muted fst-italic">Sin descripción</span>}
                      </td>
                      <td className="py-3 px-4">
                        <span className="badge bg-info text-dark px-3 py-2" style={{ borderRadius: '20px' }}>
                          {departamento.subcuenta}
                        </span>
                      </td>
                      <td className="py-3 px-4 text-center">
                        <div className="btn-group" role="group">
                          <button
                            type="button"
                            className="btn btn-sm btn-warning me-1"
                            style={{ borderRadius: '8px' }}
                            onClick={() => editDepartamento(departamento)}
                            title="Editar"
                          >
                            <i className="fas fa-edit"></i>
                          </button>
                          <button
                            type="button"
                            className="btn btn-sm btn-danger"
                            style={{ borderRadius: '8px' }}
                            onClick={() => deleteDepartamento(departamento.id)}
                            title="Eliminar"
                          >
                            <i className="fas fa-trash"></i>
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="text-center py-5">
              <div className="mb-4">
                <i className="fas fa-building" style={{ fontSize: '4rem', color: '#dee2e6' }}></i>
              </div>
              <h5 className="text-muted mb-3">No hay departamentos registrados</h5>
              <p className="text-muted mb-4">Comienza creando tu primer departamento</p>
              <button 
                className="btn btn-primary btn-lg px-4 py-2"
                style={{
                  background: 'linear-gradient(45deg, #3498db, #2980b9)',
                  border: 'none',
                  borderRadius: '10px',
                  boxShadow: '0 4px 15px rgba(52, 152, 219, 0.3)'
                }}
                onClick={() => {
                  setFormData({ nombre: '', descripcion: '', subcuenta: '' });
                  setEditingId(null);
                  setCurrentView('departamento-form');
                }}
              >
                <i className="fas fa-plus me-2"></i>
                Crear Primer Departamento
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );

  // Componente Lista de Puestos
  const PuestoList = () => (
    <div className="container-fluid">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <h2 className="mb-1" style={{ color: '#2c3e50' }}>Puestos</h2>
          <p className="text-muted mb-0">Administra los puestos de trabajo disponibles</p>
        </div>
        <button 
          className="btn btn-success btn-lg px-4 py-2"
          style={{
            background: 'linear-gradient(45deg, #27ae60, #2ecc71)',
            border: 'none',
            borderRadius: '10px',
            boxShadow: '0 4px 15px rgba(39, 174, 96, 0.3)'
          }}
          onClick={() => {
            setFormData({ nombre: '' });
            setEditingId(null);
            setCurrentView('puesto-form');
          }}
        >
          <i className="fas fa-plus me-2"></i>
          Nuevo Puesto
        </button>
      </div>

      {loading && (
        <div className="d-flex justify-content-center py-5">
          <div className="spinner-border text-success" role="status" style={{ width: '3rem', height: '3rem' }}>
            <span className="visually-hidden">Cargando...</span>
          </div>
        </div>
      )}

      <div className="card shadow-sm border-0" style={{ borderRadius: '15px' }}>
        <div className="card-body p-0">
          {puestos.length > 0 ? (
            <div className="table-responsive">
              <table className="table table-hover mb-0">
                <thead style={{ backgroundColor: '#f8f9fa' }}>
                  <tr>
                    <th className="border-0 py-3 px-4" style={{ color: '#495057', fontWeight: '600' }}>ID</th>
                    <th className="border-0 py-3 px-4" style={{ color: '#495057', fontWeight: '600' }}>Nombre</th>
                    <th className="border-0 py-3 px-4" style={{ color: '#495057', fontWeight: '600' }}>Estado</th>
                    <th className="border-0 py-3 px-4 text-center" style={{ color: '#495057', fontWeight: '600' }}>Acciones</th>
                  </tr>
                </thead>
                <tbody>
                  {puestos.map((puesto, index) => (
                    <tr key={puesto.id} style={{ 
                      backgroundColor: index % 2 === 0 ? '#ffffff' : '#f8f9fa',
                      transition: 'all 0.2s ease',
                      opacity: puesto.deleted_at ? 0.6 : 1
                    }}>
                      <td className="py-3 px-4" style={{ color: '#6c757d' }}>{puesto.id}</td>
                      <td className="py-3 px-4">
                        <span className="fw-semibold" style={{ color: '#2c3e50' }}>{puesto.nombre}</span>
                      </td>
                      <td className="py-3 px-4">
                        {puesto.deleted_at ? (
                          <span className="badge bg-danger px-3 py-2" style={{ borderRadius: '20px' }}>
                            <i className="fas fa-times me-1"></i>
                            Eliminado
                          </span>
                        ) : (
                          <span className="badge bg-success px-3 py-2" style={{ borderRadius: '20px' }}>
                            <i className="fas fa-check me-1"></i>
                            Activo
                          </span>
                        )}
                      </td>
                      <td className="py-3 px-4 text-center">
                        <div className="btn-group" role="group">
                          {!puesto.deleted_at && (
                            <>
                              <button
                                type="button"
                                className="btn btn-sm btn-warning me-1"
                                style={{ borderRadius: '8px' }}
                                onClick={() => editPuesto(puesto)}
                                title="Editar"
                              >
                                <i className="fas fa-edit"></i>
                              </button>
                              <button
                                type="button"
                                className="btn btn-sm btn-danger"
                                style={{ borderRadius: '8px' }}
                                onClick={() => deletePuesto(puesto.id)}
                                title="Eliminar"
                              >
                                <i className="fas fa-trash"></i>
                              </button>
                            </>
                          )}
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="text-center py-5">
              <div className="mb-4">
                <i className="fas fa-briefcase" style={{ fontSize: '4rem', color: '#dee2e6' }}></i>
              </div>
              <h5 className="text-muted mb-3">No hay puestos registrados</h5>
              <p className="text-muted mb-4">Comienza creando tu primer puesto de trabajo</p>
              <button 
                className="btn btn-success btn-lg px-4 py-2"
                style={{
                  background: 'linear-gradient(45deg, #27ae60, #2ecc71)',
                  border: 'none',
                  borderRadius: '10px',
                  boxShadow: '0 4px 15px rgba(39, 174, 96, 0.3)'
                }}
                onClick={() => {
                  setFormData({ nombre: '' });
                  setEditingId(null);
                  setCurrentView('puesto-form');
                }}
              >
                <i className="fas fa-plus me-2"></i>
                Crear Primer Puesto
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );

  // Componente Formulario de Departamento
  const DepartamentoForm = () => (
    <div className="container-fluid">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <h2 className="mb-1" style={{ color: '#2c3e50' }}>
            {editingId ? 'Editar Departamento' : 'Crear Departamento'}
          </h2>
          <p className="text-muted mb-0">
            {editingId ? 'Modifica la información del departamento' : 'Completa los datos del nuevo departamento'}
          </p>
        </div>
        <button 
          type="button" 
          className="btn btn-outline-secondary btn-lg px-4 py-2"
          style={{ borderRadius: '10px' }}
          onClick={() => setCurrentView('departamentos')}
        >
          <i className="fas fa-arrow-left me-2"></i>
          Volver
        </button>
      </div>

      <div className="row justify-content-center">
        <div className="col-lg-8 col-md-10">
          <div className="card shadow-sm border-0" style={{ borderRadius: '15px' }}>
            <div className="card-body p-4">
              <form onSubmit={saveDepartamento}>
                <div className="row g-4">
                  <div className="col-md-6">
                    <div className="form-floating">
                      <input
                        type="text"
                        className="form-control"
                        id="nombre"
                        name="nombre"
                        placeholder="Nombre del departamento"
                        value={formData.nombre}
                        onChange={handleInputChange}
                        required
                        style={{ borderRadius: '10px', border: '2px solid #e9ecef' }}
                      />
                      <label htmlFor="nombre" className="text-muted">
                        Nombre <span className="text-danger">*</span>
                      </label>
                    </div>
                  </div>
                  <div className="col-md-6">
                    <div className="form-floating">
                      <input
                        type="text"
                        className="form-control"
                        id="subcuenta"
                        name="subcuenta"
                        placeholder="Subcuenta (3 caracteres)"
                        value={formData.subcuenta}
                        onChange={handleInputChange}
                        maxLength="3"
                        required
                        style={{ borderRadius: '10px', border: '2px solid #e9ecef' }}
                      />
                      <label htmlFor="subcuenta" className="text-muted">
                        Subcuenta <span className="text-danger">*</span>
                      </label>
                    </div>
                  </div>
                </div>
                <div className="mt-4">
                  <div className="form-floating">
                    <textarea
                      className="form-control"
                      id="descripcion"
                      name="descripcion"
                      placeholder="Descripción del departamento"
                      rows="4"
                      value={formData.descripcion}
                      onChange={handleInputChange}
                      style={{ 
                        borderRadius: '10px', 
                        border: '2px solid #e9ecef',
                        minHeight: '120px'
                      }}
                    />
                    <label htmlFor="descripcion" className="text-muted">
                      Descripción
                    </label>
                  </div>
                </div>
                <div className="d-flex gap-3 mt-4">
                  <button 
                    type="submit" 
                    className="btn btn-primary btn-lg px-5 py-3"
                    disabled={loading}
                    style={{
                      background: 'linear-gradient(45deg, #3498db, #2980b9)',
                      border: 'none',
                      borderRadius: '10px',
                      boxShadow: '0 4px 15px rgba(52, 152, 219, 0.3)'
                    }}
                  >
                    {loading ? (
                      <>
                        <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                        Guardando...
                      </>
                    ) : (
                      <>
                        <i className="fas fa-save me-2"></i>
                        {editingId ? 'Actualizar' : 'Guardar'}
                      </>
                    )}
                  </button>
                  <button 
                    type="button" 
                    className="btn btn-outline-secondary btn-lg px-5 py-3"
                    onClick={() => setCurrentView('departamentos')}
                    style={{ borderRadius: '10px' }}
                  >
                    <i className="fas fa-times me-2"></i>
                    Cancelar
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  // Componente Formulario de Puesto
  const PuestoForm = () => (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>{editingId ? 'Editar Puesto' : 'Crear Puesto'}</h2>
        <button 
          type="button" 
          className="btn btn-secondary"
          onClick={() => setCurrentView('puestos')}
        >
          <i className="fas fa-arrow-left me-1"></i>
          Volver
        </button>
      </div>

      <div className="card">
        <div className="card-body">
          <form onSubmit={savePuesto}>
            <div className="mb-3">
              <label htmlFor="nombre" className="form-label">
                Nombre <span className="text-danger">*</span>
              </label>
              <input
                type="text"
                className="form-control"
                id="nombre"
                name="nombre"
                value={formData.nombre}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="d-flex gap-2">
              <button 
                type="submit" 
                className="btn btn-primary"
                disabled={loading}
              >
                {loading ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-1" role="status"></span>
                    Guardando...
                  </>
                ) : (
                  <>
                    <i className="fas fa-save me-1"></i>
                    {editingId ? 'Actualizar' : 'Guardar'}
                  </>
                )}
              </button>
              <button 
                type="button" 
                className="btn btn-secondary"
                onClick={() => setCurrentView('puestos')}
              >
                <i className="fas fa-times me-1"></i>
                Cancelar
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );

  // Renderizar vista actual
  const renderCurrentView = () => {
    switch (currentView) {
      case 'home':
        return <Home />;
      case 'departamentos':
        return <DepartamentoList />;
      case 'departamento-form':
        return <DepartamentoForm />;
      case 'puestos':
        return <PuestoList />;
      case 'puesto-form':
        return <PuestoForm />;
      default:
        return <Home />;
    }
  };

  return (
    <div className="App">
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <div className="container">
          <span 
            className="navbar-brand" 
            style={{ cursor: 'pointer' }}
            onClick={() => setCurrentView('home')}
          >
            Sistema CRUD
          </span>
          <button 
            className="navbar-toggler" 
            type="button" 
            data-bs-toggle="collapse" 
            data-bs-target="#navbarNav"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav me-auto">
              <li className="nav-item">
                <span 
                  className="nav-link" 
                  style={{ cursor: 'pointer' }}
                  onClick={() => setCurrentView('departamentos')}
                >
                  Departamentos
                </span>
              </li>
              <li className="nav-item">
                <span 
                  className="nav-link" 
                  style={{ cursor: 'pointer' }}
                  onClick={() => setCurrentView('puestos')}
                >
                  Puestos
                </span>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      <div className="container mt-4">
        {error && (
          <div className="alert alert-danger alert-dismissible fade show" role="alert">
            {error}
            <button 
              type="button" 
              className="btn-close" 
              onClick={() => setError(null)}
            ></button>
          </div>
        )}

        {renderCurrentView()}
      </div>
    </div>
  );
}

export default App;
