import React, { useState } from 'react';

function SimpleForm() {
  const [formData, setFormData] = useState({
    nombre: '',
    descripcion: '',
    subcuenta: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
    alert('Formulario enviado: ' + JSON.stringify(formData, null, 2));
  };

  return (
    <div style={{ padding: '20px', maxWidth: '600px', margin: '0 auto' }}>
      <h2>Formulario Simplificado - Prueba de Escritura</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '15px' }}>
          <label htmlFor="nombre" style={{ display: 'block', marginBottom: '5px' }}>
            Nombre:
          </label>
          <input
            type="text"
            id="nombre"
            name="nombre"
            value={formData.nombre}
            onChange={handleChange}
            style={{
              width: '100%',
              padding: '10px',
              border: '2px solid #ddd',
              borderRadius: '5px',
              fontSize: '16px'
            }}
            placeholder="Escribe aquí para probar..."
          />
        </div>

        <div style={{ marginBottom: '15px' }}>
          <label htmlFor="descripcion" style={{ display: 'block', marginBottom: '5px' }}>
            Descripción:
          </label>
          <textarea
            id="descripcion"
            name="descripcion"
            value={formData.descripcion}
            onChange={handleChange}
            rows="3"
            style={{
              width: '100%',
              padding: '10px',
              border: '2px solid #ddd',
              borderRadius: '5px',
              fontSize: '16px'
            }}
            placeholder="Escribe una descripción aquí..."
          />
        </div>

        <div style={{ marginBottom: '15px' }}>
          <label htmlFor="subcuenta" style={{ display: 'block', marginBottom: '5px' }}>
            Subcuenta (máx 3 caracteres):
          </label>
          <input
            type="text"
            id="subcuenta"
            name="subcuenta"
            value={formData.subcuenta}
            onChange={handleChange}
            maxLength="3"
            style={{
              width: '100%',
              padding: '10px',
              border: '2px solid #ddd',
              borderRadius: '5px',
              fontSize: '16px'
            }}
            placeholder="001"
          />
        </div>

        <button
          type="submit"
          style={{
            backgroundColor: '#007bff',
            color: 'white',
            padding: '10px 20px',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            fontSize: '16px'
          }}
        >
          Enviar Formulario
        </button>
      </form>

      <div style={{ marginTop: '20px', padding: '15px', backgroundColor: '#f8f9fa', borderRadius: '5px' }}>
        <h4>Estado Actual del Formulario:</h4>
        <pre style={{ fontSize: '14px', margin: 0 }}>
          {JSON.stringify(formData, null, 2)}
        </pre>
      </div>
    </div>
  );
}

export default SimpleForm;
