import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Configuración de la API
const API_BASE_URL = 'http://localhost:8000/api';
axios.defaults.baseURL = API_BASE_URL;

function App() {
  const [currentView, setCurrentView] = useState('home');
  const [departamentos, setDepartamentos] = useState([]);
  const [puestos, setPuestos] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Estados para formularios
  const [formData, setFormData] = useState({
    nombre: '',
    descripcion: '',
    subcuenta: ''
  });
  const [editingId, setEditingId] = useState(null);

  // Cargar datos
  const loadDepartamentos = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/departamentos');
      setDepartamentos(response.data);
    } catch (err) {
      setError('Error al cargar departamentos');
    } finally {
      setLoading(false);
    }
  };

  const loadPuestos = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/puestos');
      setPuestos(response.data);
    } catch (err) {
      setError('Error al cargar puestos');
    } finally {
      setLoading(false);
    }
  };

  // Manejar cambios en formularios
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Guardar departamento
  const saveDepartamento = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      if (editingId) {
        await axios.put(`/departamentos/${editingId}`, formData);
      } else {
        await axios.post('/departamentos', formData);
      }
      setFormData({ nombre: '', descripcion: '', subcuenta: '' });
      setEditingId(null);
      loadDepartamentos();
      setCurrentView('departamentos');
    } catch (err) {
      setError('Error al guardar departamento');
    } finally {
      setLoading(false);
    }
  };

  // Guardar puesto
  const savePuesto = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      if (editingId) {
        await axios.put(`/puestos/${editingId}`, formData);
      } else {
        await axios.post('/puestos', formData);
      }
      setFormData({ nombre: '' });
      setEditingId(null);
      loadPuestos();
      setCurrentView('puestos');
    } catch (err) {
      setError('Error al guardar puesto');
    } finally {
      setLoading(false);
    }
  };

  // Eliminar departamento
  const deleteDepartamento = async (id) => {
    if (window.confirm('¿Estás seguro de eliminar este departamento?')) {
      try {
        await axios.delete(`/departamentos/${id}`);
        loadDepartamentos();
      } catch (err) {
        setError('Error al eliminar departamento');
      }
    }
  };

  // Eliminar puesto
  const deletePuesto = async (id) => {
    if (window.confirm('¿Estás seguro de eliminar este puesto?')) {
      try {
        await axios.delete(`/puestos/${id}`);
        loadPuestos();
      } catch (err) {
        setError('Error al eliminar puesto');
      }
    }
  };

  // Editar departamento
  const editDepartamento = (departamento) => {
    setFormData({
      nombre: departamento.nombre,
      descripcion: departamento.descripcion || '',
      subcuenta: departamento.subcuenta
    });
    setEditingId(departamento.id);
    setCurrentView('departamento-form');
  };

  // Editar puesto
  const editPuesto = (puesto) => {
    setFormData({
      nombre: puesto.nombre
    });
    setEditingId(puesto.id);
    setCurrentView('puesto-form');
  };

  // Cargar datos al cambiar de vista
  useEffect(() => {
    if (currentView === 'departamentos') {
      loadDepartamentos();
    } else if (currentView === 'puestos') {
      loadPuestos();
    }
  }, [currentView]);

  // Componente Home
  const Home = () => (
    <div className="row">
      <div className="col-12">
        <div className="card">
          <div className="card-body text-center py-5">
            <h1 className="card-title mb-4">Sistema CRUD</h1>
            <p className="card-text mb-4 text-muted">
              Sistema de gestión para Departamentos y Puestos. 
              <br />
              Gestiona tus datos de manera eficiente.
            </p>
            <div className="row">
              <div className="col-md-6 mb-3">
                <div className="card h-100">
                  <div className="card-body">
                    <h5 className="card-title">
                      <i className="fas fa-building me-2"></i>
                      Departamentos
                    </h5>
                    <p className="card-text">
                      Gestiona los departamentos de tu organización con nombre, descripción y subcuenta.
                    </p>
                    <button 
                      className="btn btn-primary"
                      onClick={() => setCurrentView('departamentos')}
                    >
                      <i className="fas fa-list me-1"></i>
                      Ver Departamentos
                    </button>
                  </div>
                </div>
              </div>
              <div className="col-md-6 mb-3">
                <div className="card h-100">
                  <div className="card-body">
                    <h5 className="card-title">
                      <i className="fas fa-briefcase me-2"></i>
                      Puestos
                    </h5>
                    <p className="card-text">
                      Administra los puestos de trabajo disponibles en tu empresa.
                    </p>
                    <button 
                      className="btn btn-primary"
                      onClick={() => setCurrentView('puestos')}
                    >
                      <i className="fas fa-list me-1"></i>
                      Ver Puestos
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  // Componente Lista de Departamentos
  const DepartamentoList = () => (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Departamentos</h2>
        <button 
          className="btn btn-primary"
          onClick={() => {
            setFormData({ nombre: '', descripcion: '', subcuenta: '' });
            setEditingId(null);
            setCurrentView('departamento-form');
          }}
        >
          <i className="fas fa-plus me-1"></i>
          Nuevo Departamento
        </button>
      </div>

      {loading && (
        <div className="d-flex justify-content-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Cargando...</span>
          </div>
        </div>
      )}

      <div className="card">
        <div className="card-body">
          {departamentos.length > 0 ? (
            <div className="table-responsive">
              <table className="table table-striped">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Nombre</th>
                    <th>Descripción</th>
                    <th>Subcuenta</th>
                    <th>Acciones</th>
                  </tr>
                </thead>
                <tbody>
                  {departamentos.map((departamento) => (
                    <tr key={departamento.id}>
                      <td>{departamento.id}</td>
                      <td>{departamento.nombre}</td>
                      <td>{departamento.descripcion || 'Sin descripción'}</td>
                      <td>{departamento.subcuenta}</td>
                      <td>
                        <div className="btn-group" role="group">
                          <button
                            type="button"
                            className="btn btn-sm btn-warning"
                            onClick={() => editDepartamento(departamento)}
                          >
                            <i className="fas fa-edit"></i>
                          </button>
                          <button
                            type="button"
                            className="btn btn-sm btn-danger"
                            onClick={() => deleteDepartamento(departamento.id)}
                          >
                            <i className="fas fa-trash"></i>
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="text-center py-4">
              <p className="text-muted">No hay departamentos registrados.</p>
              <button 
                className="btn btn-primary"
                onClick={() => {
                  setFormData({ nombre: '', descripcion: '', subcuenta: '' });
                  setEditingId(null);
                  setCurrentView('departamento-form');
                }}
              >
                <i className="fas fa-plus me-1"></i>
                Crear Primer Departamento
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );

  // Componente Lista de Puestos
  const PuestoList = () => (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Puestos</h2>
        <button 
          className="btn btn-primary"
          onClick={() => {
            setFormData({ nombre: '' });
            setEditingId(null);
            setCurrentView('puesto-form');
          }}
        >
          <i className="fas fa-plus me-1"></i>
          Nuevo Puesto
        </button>
      </div>

      {loading && (
        <div className="d-flex justify-content-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Cargando...</span>
          </div>
        </div>
      )}

      <div className="card">
        <div className="card-body">
          {puestos.length > 0 ? (
            <div className="table-responsive">
              <table className="table table-striped">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Nombre</th>
                    <th>Estado</th>
                    <th>Acciones</th>
                  </tr>
                </thead>
                <tbody>
                  {puestos.map((puesto) => (
                    <tr key={puesto.id} className={puesto.deleted_at ? 'table-secondary' : ''}>
                      <td>{puesto.id}</td>
                      <td>{puesto.nombre}</td>
                      <td>
                        {puesto.deleted_at ? (
                          <span className="badge bg-danger">Eliminado</span>
                        ) : (
                          <span className="badge bg-success">Activo</span>
                        )}
                      </td>
                      <td>
                        <div className="btn-group" role="group">
                          {!puesto.deleted_at && (
                            <>
                              <button
                                type="button"
                                className="btn btn-sm btn-warning"
                                onClick={() => editPuesto(puesto)}
                              >
                                <i className="fas fa-edit"></i>
                              </button>
                              <button
                                type="button"
                                className="btn btn-sm btn-danger"
                                onClick={() => deletePuesto(puesto.id)}
                              >
                                <i className="fas fa-trash"></i>
                              </button>
                            </>
                          )}
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="text-center py-4">
              <p className="text-muted">No hay puestos registrados.</p>
              <button 
                className="btn btn-primary"
                onClick={() => {
                  setFormData({ nombre: '' });
                  setEditingId(null);
                  setCurrentView('puesto-form');
                }}
              >
                <i className="fas fa-plus me-1"></i>
                Crear Primer Puesto
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );

  // Componente Formulario de Departamento
  const DepartamentoForm = () => (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>{editingId ? 'Editar Departamento' : 'Crear Departamento'}</h2>
        <button 
          type="button" 
          className="btn btn-secondary"
          onClick={() => setCurrentView('departamentos')}
        >
          <i className="fas fa-arrow-left me-1"></i>
          Volver
        </button>
      </div>

      <div className="card">
        <div className="card-body">
          <form onSubmit={saveDepartamento}>
            <div className="row">
              <div className="col-md-6">
                <div className="mb-3">
                  <label htmlFor="nombre" className="form-label">
                    Nombre <span className="text-danger">*</span>
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="nombre"
                    name="nombre"
                    value={formData.nombre}
                    onChange={handleInputChange}
                    required
                  />
                </div>
              </div>
              <div className="col-md-6">
                <div className="mb-3">
                  <label htmlFor="subcuenta" className="form-label">
                    Subcuenta <span className="text-danger">*</span>
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="subcuenta"
                    name="subcuenta"
                    value={formData.subcuenta}
                    onChange={handleInputChange}
                    maxLength="3"
                    required
                  />
                </div>
              </div>
            </div>
            <div className="mb-3">
              <label htmlFor="descripcion" className="form-label">
                Descripción
              </label>
              <textarea
                className="form-control"
                id="descripcion"
                name="descripcion"
                rows="3"
                value={formData.descripcion}
                onChange={handleInputChange}
              />
            </div>
            <div className="d-flex gap-2">
              <button 
                type="submit" 
                className="btn btn-primary"
                disabled={loading}
              >
                {loading ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-1" role="status"></span>
                    Guardando...
                  </>
                ) : (
                  <>
                    <i className="fas fa-save me-1"></i>
                    {editingId ? 'Actualizar' : 'Guardar'}
                  </>
                )}
              </button>
              <button 
                type="button" 
                className="btn btn-secondary"
                onClick={() => setCurrentView('departamentos')}
              >
                <i className="fas fa-times me-1"></i>
                Cancelar
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );

  // Componente Formulario de Puesto
  const PuestoForm = () => (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>{editingId ? 'Editar Puesto' : 'Crear Puesto'}</h2>
        <button 
          type="button" 
          className="btn btn-secondary"
          onClick={() => setCurrentView('puestos')}
        >
          <i className="fas fa-arrow-left me-1"></i>
          Volver
        </button>
      </div>

      <div className="card">
        <div className="card-body">
          <form onSubmit={savePuesto}>
            <div className="mb-3">
              <label htmlFor="nombre" className="form-label">
                Nombre <span className="text-danger">*</span>
              </label>
              <input
                type="text"
                className="form-control"
                id="nombre"
                name="nombre"
                value={formData.nombre}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="d-flex gap-2">
              <button 
                type="submit" 
                className="btn btn-primary"
                disabled={loading}
              >
                {loading ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-1" role="status"></span>
                    Guardando...
                  </>
                ) : (
                  <>
                    <i className="fas fa-save me-1"></i>
                    {editingId ? 'Actualizar' : 'Guardar'}
                  </>
                )}
              </button>
              <button 
                type="button" 
                className="btn btn-secondary"
                onClick={() => setCurrentView('puestos')}
              >
                <i className="fas fa-times me-1"></i>
                Cancelar
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );

  // Renderizar vista actual
  const renderCurrentView = () => {
    switch (currentView) {
      case 'home':
        return <Home />;
      case 'departamentos':
        return <DepartamentoList />;
      case 'departamento-form':
        return <DepartamentoForm />;
      case 'puestos':
        return <PuestoList />;
      case 'puesto-form':
        return <PuestoForm />;
      default:
        return <Home />;
    }
  };

  return (
    <div className="App">
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <div className="container">
          <span 
            className="navbar-brand" 
            style={{ cursor: 'pointer' }}
            onClick={() => setCurrentView('home')}
          >
            Sistema CRUD
          </span>
          <button 
            className="navbar-toggler" 
            type="button" 
            data-bs-toggle="collapse" 
            data-bs-target="#navbarNav"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav me-auto">
              <li className="nav-item">
                <span 
                  className="nav-link" 
                  style={{ cursor: 'pointer' }}
                  onClick={() => setCurrentView('departamentos')}
                >
                  Departamentos
                </span>
              </li>
              <li className="nav-item">
                <span 
                  className="nav-link" 
                  style={{ cursor: 'pointer' }}
                  onClick={() => setCurrentView('puestos')}
                >
                  Puestos
                </span>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      <div className="container mt-4">
        {error && (
          <div className="alert alert-danger alert-dismissible fade show" role="alert">
            {error}
            <button 
              type="button" 
              className="btn-close" 
              onClick={() => setError(null)}
            ></button>
          </div>
        )}

        {renderCurrentView()}
      </div>
    </div>
  );
}

export default App;
