import React, { useState } from 'react';

function DirectForm() {
  const [nombre, setNombre] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [subcuenta, setSubcuenta] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = {
      nombre,
      descripcion,
      subcuenta
    };
    console.log('Form submitted:', formData);
    alert('Formulario enviado: ' + JSON.stringify(formData, null, 2));
  };

  return (
    <div style={{ padding: '20px', maxWidth: '600px', margin: '0 auto' }}>
      <h2>Formulario con Estados Separados - Prueba de Escritura</h2>
      <p style={{ color: '#666', marginBottom: '20px' }}>
        Este formulario usa estados separados para cada campo en lugar de un objeto.
      </p>
      
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '15px' }}>
          <label htmlFor="nombre" style={{ display: 'block', marginBottom: '5px' }}>
            Nombre:
          </label>
          <input
            type="text"
            id="nombre"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
            style={{
              width: '100%',
              padding: '10px',
              border: '2px solid #ddd',
              borderRadius: '5px',
              fontSize: '16px'
            }}
            placeholder="Escribe aquí para probar (estados separados)..."
          />
        </div>

        <div style={{ marginBottom: '15px' }}>
          <label htmlFor="descripcion" style={{ display: 'block', marginBottom: '5px' }}>
            Descripción:
          </label>
          <textarea
            id="descripcion"
            value={descripcion}
            onChange={(e) => setDescripcion(e.target.value)}
            rows="3"
            style={{
              width: '100%',
              padding: '10px',
              border: '2px solid #ddd',
              borderRadius: '5px',
              fontSize: '16px'
            }}
            placeholder="Escribe una descripción aquí (estados separados)..."
          />
        </div>

        <div style={{ marginBottom: '15px' }}>
          <label htmlFor="subcuenta" style={{ display: 'block', marginBottom: '5px' }}>
            Subcuenta (máx 3 caracteres):
          </label>
          <input
            type="text"
            id="subcuenta"
            value={subcuenta}
            onChange={(e) => setSubcuenta(e.target.value)}
            maxLength="3"
            style={{
              width: '100%',
              padding: '10px',
              border: '2px solid #ddd',
              borderRadius: '5px',
              fontSize: '16px'
            }}
            placeholder="001"
          />
        </div>

        <button
          type="submit"
          style={{
            backgroundColor: '#dc3545',
            color: 'white',
            padding: '10px 20px',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            fontSize: '16px'
          }}
        >
          Enviar Formulario (Estados Separados)
        </button>
      </form>

      <div style={{ marginTop: '20px', padding: '15px', backgroundColor: '#f8f9fa', borderRadius: '5px' }}>
        <h4>Estado Actual del Formulario:</h4>
        <pre style={{ fontSize: '14px', margin: 0 }}>
          {JSON.stringify({ nombre, descripcion, subcuenta }, null, 2)}
        </pre>
      </div>
    </div>
  );
}

export default DirectForm;
